import React, { useEffect, useRef, useState } from "react";
import PropTypes from "prop-types";
// css
import "./Videos.scss";
// video
import lamboVideo from "../../assets/videos/lambo_gift.mp4";

function Videos(props) {
  const videoRef = useRef();
  const videoContainerRef = useRef();
  const [isPlaying, setIsPlaying] = useState(true);
  const [isTheaterMode, setIsTheaterMode] = useState(false);

  // play / pause
  const playPauseHandler = () => {
    setIsPlaying(videoRef.current.paused);

    if (videoRef.current.paused) {
      videoRef.current.play();
      return;
    }
    videoRef.current.pause();
  };

  // video keyboard event
  const keyDownHandler = (e) => {
    switch (e.key) {
      case " ":
      case "k":
        playPauseHandler();
        break;
      default:
        break;
    }
  };

  // theater mode
  const toggleTheaterHandler = () => {
    setIsTheaterMode((prevState) => !prevState);
  };

  // full screen
  const toggleFullScreenHandler = () => {
    if (!document.fullscreenElement) {
      videoRef.current.requestFullScreen();
      return;
    }

    document.exitFullscreen();
  };

  const toggleMiniPlayerMode = () => {};

  useEffect(() => {
    document.addEventListener("keydown", (e) => {
      keyDownHandler(e);
    });
  }, []);

  return (
    <main>
      <div
        ref={videoContainerRef}
        className={
          "video-container " +
          (isPlaying ? "" : "paused ") +
          (isTheaterMode ? "theater" : "")
        }
      >
        {/* video controls overlay */}
        <div className="video-container__controls ">
          {/* overlay timeline */}
          <div className="video-container__controls__timeline"></div>
          {/* controls */}
          <div className="controls">
            {/* controlling play and pause */}
            <button
              className="controls__play-pause"
              onClick={() => playPauseHandler()}
            >
              <svg className="play-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
              </svg>
              <svg className="pause-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
              </svg>
            </button>
            <button
              className="controls__mini-play"
              onClick={() => toggleMiniPlayerMode()}
            >
              <svg viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M21 3H3c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h18c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H3V5h18v14zm-10-7h9v6h-9z"
                />
              </svg>
            </button>
            <button
              className="controls__threater"
              onClick={() => toggleTheaterHandler()}
            >
              {isTheaterMode ? (
                <svg className="wide" viewBox="0 0 24 24">
                  <path
                    fill="currentColor"
                    d="M19 7H5c-1.1 0-2 .9-2 2v6c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V9c0-1.1-.9-2-2-2zm0 8H5V9h14v6z"
                  />
                </svg>
              ) : (
                <svg className="tall" viewBox="0 0 24 24">
                  <path
                    fill="currentColor"
                    d="M19 6H5c-1.1 0-2 .9-2 2v8c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V8c0-1.1-.9-2-2-2zm0 10H5V8h14v8z"
                  />
                </svg>
              )}
            </button>
            <button
              className="controls-full-screen"
              onClick={() => toggleFullScreenHandler()}
            >
              <svg className="open" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M7 14H5v5h5v-2H7v-3zm-2-4h2V7h3V5H5v5zm12 7h-3v2h5v-5h-2v3zM14 5v2h3v3h2V5h-5z"
                />
              </svg>
              <svg className="close" viewBox="0 0 24 24">
                <path
                  fill="currentColor"
                  d="M5 16h3v3h2v-5H5v2zm3-8H5v2h5V5H8v3zm6 11h2v-3h3v-2h-5v5zm2-11V5h-2v5h5V8h-3z"
                />
              </svg>
            </button>
          </div>
        </div>
        {/* video itself */}
        <video ref={videoRef} autoPlay muted onClick={() => playPauseHandler()}>
          <source src={lamboVideo} type="video/mp4" />
        </video>
      </div>
    </main>
  );
}

Videos.propTypes = {};

export default Videos;
