import React, { useRef, useState } from "react";
import PropTypes from "prop-types";
// css
import "./Videos.scss";
// video
import lamboVideo from "../../assets/videos/lambo_gift.mp4";

function Videos(props) {
  const videoRef = useRef();
  const [isPlaying, setIsPlaying] = useState(true);

  // play / pause
  const playPauseHandler = () => {
    setIsPlaying(videoRef.current.paused);

    if (videoRef.current.paused) {
      videoRef.current.play();
      return;
    }
    videoRef.current.pause();
  };

  // video keyboard event
  const keyDownHandler = (e) => {
    switch (e.key) {
      case " ":
      case "k":
        playPauseHandler();
        break;
      default:
        break;
    }
  };

  return (
    <main onKeyDown={(e) => keyDownHandler(e)}>
      <div className={"video-container " + (isPlaying ? "" : "paused")}>
        {/* video controls overlay */}
        <div className="video-container__controls ">
          {/* overlay timeline */}
          <div className="video-container__controls__timeline"></div>
          {/* controls */}
          <div className="controls">
            <button
              className="controls__play-pause"
              onClick={() => playPauseHandler()}
            >
              <svg className="play-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
              </svg>
              <svg className="pause-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
              </svg>
            </button>
          </div>
        </div>
        {/* video itself */}
        <video ref={videoRef} autoPlay muted onClick={() => playPauseHandler()}>
          <source src={lamboVideo} type="video/mp4" />
        </video>
      </div>
    </main>
  );
}

Videos.propTypes = {};

export default Videos;
